package Review_Arrays;

public class nestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// the nested for loop repeats the code repeatedly
		// Why using nested for loop? what is that piece of code that can't be written without nested loop?
		
		// 
		
		for (int y=0; y<3; y++) {
			for (int x=0; x<2; x++) {
				
				System.out.println("This is a great time");
			}
		}

			
	}

}
