package com.Syntax_Class07;

public class Task_02_Using_While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=1;
		
		while(num<100){
		
		System.out.print(num+" ");
		num++;
		}
		
		int a=100;
		
		while(a>=1) {
			System.out.print(a);
			a--;
		}
		
		int even=20;
		while(even<100) {
			System.out.print(even);
			even+=2;
		}
		
	}
}
