package com.Syntax_Class07;

public class TheForLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		
		
		for (int i=1; i<=5; i++) {
			System.out.println("hello");
			
			
	
			
			
		}
		
		for (int i=10; i<=25; i++) {
			System.out.print(i+" ");
		}
		
		for (int i=10; i<=25; i++) {
			System.out.print(i+" ");
		}
		
		
		
		for (int i =50; i>=1; i--) {
			System.out.print(i+" ");
		}
	
	}

}
