package comsyntaxclass02;

public class CreatingVaraibles {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/* create a variable and assign value
		 *  Declare a variable and initialize
		 * we use declaration and initialization
		 */
		int age=25;
		
		
		
		//can I change a variable value?Yes
		// by recreating value we can change it..without deletion
		
		age=26;
		// reassigned the value of variable age
		
		System.out.println(age);
		
		age=57;
		age=75;	
		System.out.println(age);
		
		age=100;
		//java Rules=False;ERROR-->do not use key as identifier
		//boolean break=false/error-->do not use keywords as identifier
		//char 1Character=do not start identifier or special characters
		
		int age1=25;
		System.out.println(age1);
		age1=28;
		age1=48;
				
		System.out.println(age1);
		System.out.println(age1);
		
		
		
		
		
		
		
		
	}

}
