package homeWorkClass07;

public class SumLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int sum=0;
		
		for (int i=0;  i<=5; i++) {
			
			sum+=i;
		}
		System.out.println(sum);

		
		System.out.println("What is the result?");
		
		int value=1;
		
		for(int i=1; i<4; i++) {
			value*=i;
			
		}
		System.out.println(value);
	}

}
