package reviewClassProgramming;

public class ArthemiticDemot {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		/*
		 * Arithmetic is + - *
		 * 
		 * The operation is also implements on the alphabet also 
		 * Java doesn't have operator to subtract string
		 * 
		 */
		
		int number1=10;
		int number2=10;
		System.out.println(number1+number2);
		
		String name1="Abdul";
		String name2="Zuhoor";
		System.out.println(name1+name2);
		/**********************************************/
		
		char letter1='A';	// the value in the Asci defined the value of A is 65 a
		char letter2='B';	// the value in the Asci defined the value of A is 66 a
		System.out.println(letter1-letter2);	// not defined
		/************************************************************************/
		
		

		
		System.out.println(letter1*letter2);	//not defined char
		/*****************************************************/
		
		System.out.println(number1*number2); //defined int
		
		// multiplication is not defined for string

		
		
		
	
		
		
		
		
		
		
		
	}

}
