package reviewClassProgramming;

public class Operators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int number=10;
		System.out.println(++number);
		
		System.out.println(number++);
		
		System.out.println(number);
		
		
		//++ means increment by one the value.
		/* in line 8 we are doing two tasks addition and printing
		 * when adding ++ sign at the beg it means do addition then the print
		 * if comes at the end it indicates first operation then the increment.
		 */
		
		
	}

}
