package reviewClassProgramming;

public class Shortening {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int number=10;
		number=number+1;
		System.out.println(number);
		
		// shortcut for the above is ----- if we have increase the number by one we do ++		
		
		number++;
		System.out.println(number);
		number+=10;
		System.out.println(number);
		number=100;
		System.out.println(number);
		
		// if use subtract we decremented by that number.
		
		number-=100;
		System.out.println(number);
		
		number=10;
		++number;
		System.out.println(number);
		
		number++;
		System.out.println(number);

		
		
				

	}

}
